import { Component } from '@angular/core';

@Component({
  selector: 'app-contact',
  templateUrl: './contact.component.html',
  styleUrls: ['./contact.component.css']
})
export class ContactComponent {
  name: string = '';
  email: string = '';
  message: string = '';
onSubmit: any;

  submitContact() {
    // Implement logic to submit the contact form
    console.log('Name:', this.name);
    console.log('Email:', this.email);
    console.log('Message:', this.message);
  }
}


  <div class="contact-us">
    <h1>Contact Us</h1>



    <div class="hours-of-operation">
      <h2>Hours of Operation</h2>
      <p><strong>Monday - Friday:</strong> 8:00 AM - 6:00 PM</p>
      <p><strong>Saturday:</strong> 10:00 AM - 4:00 PM</p>
      <p><strong>Sunday:</strong> Closed</p>
    </div>

    <div class="contact-information">
      <h2>Contact Information</h2>
      <p><strong>Phone:</strong> (123) 456-7890
      +263 775125332</p>
      <p><strong>Email:</strong> <a href="mailto:studentclinic@university.edu">matangipride04@gmail.com</a></p>
    </div>


    <div class="emergency-contact">
      <h2>Emergency Contact</h2>
      <p>For medical emergencies, please dial 911 or visit the nearest emergency room.</p>
    </div>

    <div class="social-media">
      <h2>Social Media</h2>
      <p>Stay connected with us through our social media platforms:</p>
      <ul>
        <li><a href="https://www.facebook.com/universityclinic">Facebook</a></li>
        <li><a href="https://www.twitter.com/universityclinic">Twitter</a></li>
        <li><a href="https://www.instagram.com/universityclinic">Instagram</a></li>
      </ul>
    </div>
