import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-std',
  templateUrl: './std.component.html',
  styleUrls: ['./std.component.css']
})
export class StdComponent {
  stdList = [
    { name: 'Chlamydia', symptoms: '...', prevention: '...', getHelp: '...' },
    { name: 'Gonorrhea', symptoms: '...', prevention: '...', getHelp: '...' },
    // Add more STDs here
  ];

  constructor(private router: Router) {}

  getHelp(std: any) {
    // Navigate to a new component with the STD details
    this.router.navigate(['/std', std.name]);
  }
}
<body><h1>STD</h1>
  <table class="table table-striped">
    <thead>
      <tr>
        <th>Name</th>
        <th>Symptoms</th>
        <th>Prevention</th>
        <th>Get Help</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let std of stdList">
        <td>{{ std.name }}</td>
        <td>{{ std.symptoms }}</td>
        <td>{{ std.prevention }}</td>
        <td><button (click)="getHelp(std)" class="btn btn-primary">Get Help</button></td>
      </tr>
    </tbody>
  </table>
</body>
